import React from "react";
import Input from "../Utils/Input";
import Section from "../Utils/Section";

const Personal = ({ personalInfo, onChange }) => {
  return (
    <Section title="Personal Information" padding="2rem 0">
      <Input
        type="text"
        name="firstName"
        placeholder="First Name"
        onChange={onChange}
        value={personalInfo.name}
      />
      <Input
        type="text"
        name="latName"
        placeholder="Last Name"
        onChange={onChange}
        value={personalInfo.name}
      />
      <Input
        type="text"
        name="email"
        placeholder="Email"
        onChange={onChange}
        value={personalInfo.email}
      />
      <Input
        type="text"
        name="phone"
        placeholder="Phone"
        onChange={onChange}
        value={personalInfo.phone}
      />
      <Input
        type="text"
        name="address"
        placeholder="Address"
        onChange={onChange}
        value={personalInfo.address}
      />
      <TextArea
        name="summary"
        placeholder="Summary"
        onChange={onChange}
        value={personalInfo.summary}
      />
    </Section>
  );
};

export default Personal;
